import React, { useEffect, useState } from 'react'

import { Button, ButtonGroup, Table  } from 'react-bootstrap';
import { useStore } from '../store/store';
import { useLazyQuery, useQuery } from '@apollo/client';
import { GET_ALL_USERS } from '../utils/graphql';
import { Result } from './Result';

export const TableUsers = ({users}) => {
    const [{ auth }] = useStore();

    const {checkeds, handleChange, handleChangeAll, handleChangeNo} = useCheckbox(users)

  const {data, loading, error}= useQuery(GET_ALL_USERS)
  // console.log(data);

  const allUsers = data?.allUsers


    return (
        <Table>
            <thead>
              <tr>
                <th>
                <ButtonGroup size="sm">
                  <Button onClick={handleChangeAll}>
                  All
                  </Button>
                  <Button onClick={handleChangeNo}>
                  No
                  </Button>
                </ButtonGroup>

                </th>
                <th>Id</th>
                <th>Name</th>
                <th>Email</th>
                <th>Date</th>
                <th>Last Login</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
        <Result data={allUsers}/>
        </tbody>
        </Table>
    )
}
