import React, { useEffect, useState } from 'react'
import useCheckbox from '../hooks/useCheckbox';
import { Checkbox } from './Checkbox';
import { Button, ButtonGroup, Table  } from 'react-bootstrap';
import { useStore } from '../store/store';


export const TableUsers = ({users}) => {
    const [{ auth }] = useStore();
    const [user, setUser] = useState([])

    const {checkeds, handleChange, handleChangeAll, handleChangeNo} = useCheckbox(users)

  useEffect(()=>{
    setUser(users) 
  }, [users])
  console.log(user);
  function conversionDate(data) {
    let date = new Date(+data);
    date = date.toString().split(' ')
    return `${date[2]} ${date[1]} ${date[3]} ${date[4]}  `;
  }
    return (
        <Table>
            <thead>
              <tr>
                <th>
                <ButtonGroup size="sm">
                  <Button onClick={handleChangeAll}>
                  All
                  </Button>
                  <Button onClick={handleChangeNo}>
                  No
                  </Button>
                </ButtonGroup>

                </th>
                <th>Id</th>
                <th>Name</th>
                <th>Email</th>
                <th>Date</th>
                <th>Last Login</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
        { user.map((u, i)=> {
            return (
                <tr key={u.id}>
                  <td>
                  <Checkbox name={u.id} checked={checkeds[u.id]} onChange={handleChange} />
                  </td>
                  <td>{u.id}</td>
                  <td>{u.name}</td>
                  <td>{u.email}</td>
                  <td>{conversionDate(u.createAt)}</td>
                  <td>{conversionDate(u.dateLastLogin)}</td>
                  <td>{u.active === true ? 'Active' : 'Not Active'}</td>
                </tr>
            )
        })}
        </tbody>
        </Table>
    )
}
