import React from 'react';
import PropTypes from 'prop-types';
import { Switch, Route, Redirect } from 'react-router-dom';
import styled from 'styled-components';

import { Overlay } from 'components/Layout';
import { AuthHeader, SignUp, ResetPassword } from 'pages/Auth';

import * as Routes from 'routes';

import backgroundImage from './background.jpeg';

const Root = styled.div`
  background: url(${backgroundImage}) no-repeat top / cover;
  width: 100%;
  height: 100vh;
`;

const Container = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  z-index: ${(p) => p.theme.zIndex.lg};
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  @media (min-width: ${(p) => p.theme.screen.md}) {
    justify-content: center;
  }
`;

const Pages = styled.div`
  margin-top: 80px;
  @media (min-width: ${(p) => p.theme.screen.md}) {
    margin-top: -120px;
  }
`;

/**
 * Main Layout for the app, when user isn't authenticated
 */
const AuthLayout = ({ refetch }) => {
  return (
    <>
        <AuthHeader refetch={refetch} />
        <Pages>
          <Switch>
            <Route exact path="/" render={() => <SignUp refetch={refetch} />} />
            <Route exact path={Routes.FORGOT_PASSWORD} component={ForgotPassword} />
            <Route exact path={Routes.RESET_PASSWORD} render={() => <ResetPassword refetch={refetch} />} />
            <Redirect to="/" />
          </Switch>
        </Pages>
    </>
  );
};

AuthLayout.propTypes = {
  refetch: PropTypes.func.isRequired,
};

export default AuthLayout;
